// Student.js

import React, { useState, useEffect } from 'react';
import './Student.css';

function Student() {
  const [studentData, setStudentData] = useState({
    name: '',
    rollNo: '',
    class: '',
  });
  const [data, setData]= useState([])
  useEffect(() => {
    fetch('http://localhost:3500/student')
      .then((res) => res.json())
      .then((data) => setData(data))
      .catch((err) => console.log(err));
  }, []); // Provide an empty dependency array  

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setStudentData({ ...studentData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here (e.g., send data to a server or store locally).
    console.log('Student data submitted:', studentData);

    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify( studentData )
  };

    fetch('http://localhost:3500/addstudent', requestOptions ).then((res) => res.json())
.then(() => console.log("data added!"))
      .catch((err) => console.log(err));
  };

  return (
    <div>
    <div className="student-container">
      <div className="content">
        <h2 className='student-details'>Student Details</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={studentData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Roll No (Must be Number):</label>
            <input
              type="text"
              name="rollNo"
              value={studentData.rollNo}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Class:</label>
            <input
              type="text"
              name="class"
              value={studentData.class}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
    </div>
    <div style={{ display: 'flex', justifyContent: 'flex-end', padding: '50px 0' }}>
      <h2 className='student-details'>Student details</h2>
      <table style={{ border: '1px solid #ccc', borderCollapse: 'collapse', margin: '100px 0',marginRight: '50px', marginTop: '200px', width: '40%', backgroundColor: '#f4f4f4' }}>
        <thead>
          <tr style={{ backgroundColor: '#ddd' }}>
            <th style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'left' }}>Name</th>
            <th style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'left' }}>Roll Number</th>
            <th style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'left' }}>Class</th>
          </tr>
        </thead>
        <tbody>
          {data.map((d, i) => (
            <tr key={i} style={{ backgroundColor: i % 2 === 0 ? '#f9f9f9' : '#fff' }}>
              <td style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'left' }}>{d.name}</td>
              <td style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'left' }}>{d.roll_number}</td>
              <td style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'left' }}>{d.class}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </div>
  );
}

export default Student;
