import React, { useState, useEffect } from 'react';
import './Student.css';

function Student() {
  const [studentData, setStudentData] = useState({
    name: '',
    rollNo: '',
    class: '',
  });
  const [data, setData] = useState([]);

  // Function to fetch data
  const fetchData = () => {
    fetch('http://localhost:3500/student')
      .then((res) => res.json())
      .then((data) => setData(data))
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    // Fetch data initially
    fetchData();
  }, []); // Provide an empty dependency array

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setStudentData({ ...studentData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here (e.g., send data to a server or store locally).
    console.log('Student data submitted:', studentData);

    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(studentData),
    };

    fetch('http://localhost:3500/addstudent', requestOptions)
      .then((res) => res.json())
      .then(() => {
        console.log('Data added!');
        // Fetch data again after successful submission
        fetchData();
      })
      .catch((err) => console.log(err));
  };

  return (
    <div>
      <div className="student-container">
        <div className="content">
          <h2 className='store-student-details'>Student Details</h2>
          <form onSubmit={handleSubmit}>
            {/* ... Your form inputs ... */}
          </form>
        </div>
        <div style={{ display: 'flex', justifyContent: 'flex-end', padding: '50px 0' }}>
          <h2 className='student-details'>Student details</h2>
          <table style={{ /* ... Your table styles ... */ }}>
            {/* ... Table headers and rows ... */}
          </table>
        </div>
      </div>
    </div>
  );
}

export default Student;
