// Teacher.js

import React, { useState, useEffect } from 'react';
import './Teacher.css';

function Teacher() {
  const [TeacherData, setTeacherData] = useState({
    name: '',
    subject: '',
    class: ''
  });

  const [data, setData]= useState([])

  const getData=()=> {
    fetch('http://localhost:3500/student')
    .then((res) => res.json())
    .then((data) => setData(data))
    .catch((err) => console.log(err));
  }

  useEffect(() => {
    getData()
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTeacherData({ ...TeacherData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here (e.g., send data to a server or store locally).
    console.log('Teacher data submitted:', TeacherData);
    setTeacherData({
      name: '',
      subject: '',
      class: '',
    });


  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify( TeacherData )
};

  fetch('http://localhost:3500/addteacher', requestOptions ).then((res) => res.json())
.then(() => {console.log("data added!")
    getData()}
)
    .catch((err) => console.log(err));
};

const handleDelete = (id) => {
  fetch(`http://localhost:3500/teacher/${id}`, {
    method: 'DELETE',
  })
    .then((res) => res.json())
    .then(() => {
      getData();
    })
    .catch((err) => console.error('Error deleting data:', err));
};

  return (
    <div className="Teacher-container">
      <div className="content">
        <h2>Teacher Details</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={TeacherData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Subject:</label>
            <input
              type="text"
              name="subject"
              value={TeacherData.subject}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Class:</label>
            <input
              type="text"
              name="class"
              value={TeacherData.class}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
      <div style={{ display: 'flex', justifyContent: 'flex-end', padding: '50px 0' }}>
      <h2 className='teacher-details'>Teacher details</h2>
      <table style={{ border: '1px solid #ccc', borderCollapse: 'collapse', margin: '100px 0',marginRight: '50px', marginTop: '200px', width: '40%', backgroundColor: '#f4f4f4' }}>
        <thead>
          <tr style={{ backgroundColor: '#ddd' }}>
            <th style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'center' }}>Name</th>
            <th style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'center' }}>Subject</th>
            <th style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'center' }}>Class</th>
          </tr>
        </thead>
        <tbody>
          {data.map((d, i) => (
            <tr key={i} style={{ backgroundColor: i % 2 === 0 ? '#f9f9f9' : '#fff' }}>
              <td style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'center' }}>{d.name}</td>
              <td style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'center' }}>{d.subject}</td>
              <td style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'center' }}>{d.class}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </div>
  );
}

export default Teacher;
