// Teacher.js

import React, { useState, useEffect } from 'react';
import './Teacher.css';

function Teacher() {
  const [TeacherData, setTeacherData] = useState({
    name: '',
    subject: '',
    class: ''
  });

  const [data, setData]= useState([])
  useEffect(() => {
    fetch('http://localhost:3500/teacher')
      .then((res) => res.json())
      .then((data) => setData(data))
      .catch((err) => console.log(err));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTeacherData({ ...TeacherData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here (e.g., send data to a server or store locally).
    console.log('Teacher data submitted:', TeacherData);


  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify( TeacherData )
};

  fetch('http://localhost:3500/addteacher', requestOptions ).then((res) => res.json())
.then(() => console.log("data added!"))
    .catch((err) => console.log(err));
};

  return (
    <div className="Teacher-container">
      <div className="content">
        <h2>Teacher Details</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={TeacherData.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Subject:</label>
            <input
              type="text"
              name="subject"
              value={TeacherData.subject}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Class:</label>
            <input
              type="text"
              name="class"
              value={TeacherData.class}
              onChange={handleInputChange}
            />
          </div>
          <div className="form-group">
            <button type="submit">Submit</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Teacher;
